DROP DATABASE IF EXISTS GameStore
CREATE DATABASE GameStore
USE GameStore

/*
Drop Table platform
Drop Table game
Drop Table game_platform
Drop Table country
Drop Table city
Drop Table address
Drop Table store
Drop Table staff
Drop Table inventory
Drop Table orderorder
Drop Table inventory_order
Drop Table customer
*/

CREATE TABLE platform(
    platform_id int IDENTITY(1,1) NOT NULL,
    name varchar(45) NULL,
    CONSTRAINT pk_platform PRIMARY KEY(platform_id)
)

CREATE TABLE game(
    game_id int IDENTITY(1,1) NOT NULL,
    name varchar(128) NOT NULL,
    rating DECIMAL(4,2) NOT NULL DEFAULT 4.99,
    release_date DATE DEFAULT NULL,
    price DECIMAL(5,2) NOT NULL DEFAULT 19.99,
    description TEXT DEFAULT NULL,
    players_count int NULL,
    recommendation_count int NULL,
    CONSTRAINT pk_game PRIMARY KEY(game_id)
)



CREATE TABLE game_platform(
    game_platform_id int IDENTITY(1,1) NOT NULL,
    game_id int NOT NULL,
    platform_id int NOT NULL,
    CONSTRAINT pk_game_platform PRIMARY KEY(game_platform_id),
    CONSTRAINT fk1_game_platform FOREIGN KEY (game_id) REFERENCES game(game_id),
    CONSTRAINT fk2_game_platform FOREIGN KEY (platform_id) REFERENCES platform(platform_id)
)



CREATE TABLE country (
  country_id int IDENTITY(1,1) NOT NULL,
  country_name VARCHAR(50) NOT NULL,
  CONSTRAINT pk_country PRIMARY KEY  (country_id)
)


CREATE TABLE city (
  city_id int IDENTITY(1,1) NOT NULL,
  city_name VARCHAR(50) NOT NULL,
  country_id int NOT NULL,
  CONSTRAINT pk_city PRIMARY KEY (city_id),
  CONSTRAINT fk_city FOREIGN KEY (country_id) REFERENCES country (country_id)
)


CREATE TABLE address (
  address_id int IDENTITY(1,1) NOT NULL,
  address VARCHAR(300) NOT NULL,
  district VARCHAR(20) NOT NULL,
  city_id int NOT NULL,
  postal_code VARCHAR(20) DEFAULT NULL,
  CONSTRAINT pk_address PRIMARY KEY (address_id),
  CONSTRAINT fk_address FOREIGN KEY (city_id) REFERENCES city (city_id)
)


CREATE TABLE store (
  store_id int IDENTITY(1,1) NOT NULL,
  address_id int NOT NULL,
  CONSTRAINT pk_store PRIMARY KEY  (store_id),
  CONSTRAINT fk_store FOREIGN KEY (address_id) REFERENCES address (address_id)
)

CREATE TABLE staff (
  staff_id int IDENTITY(1,1) NOT NULL,
  manager_staff_id int NULL,
  first_name VARCHAR(45) NOT NULL,
  last_name VARCHAR(45) NOT NULL,
  email VARCHAR(50) DEFAULT NULL,
  store_id int NOT NULL,
  CONSTRAINT pk_staff PRIMARY KEY  (staff_id),
  CONSTRAINT fk1_staff FOREIGN KEY (store_id) REFERENCES store (store_id),
  CONSTRAINT fk2_staff FOREIGN KEY (manager_staff_id) REFERENCES staff (staff_id)
)


CREATE TABLE inventory(
    inventory_id int IDENTITY(1,1) NOT NULL,
    game_id int NOT NULL,
    store_id int NOT NULL,
    stock_quantity int NULL,
    CONSTRAINT pk_inventory PRIMARY KEY(inventory_id),
    CONSTRAINT fk1_inventory FOREIGN KEY (game_id) REFERENCES game(game_id),
    CONSTRAINT f2k_inventory FOREIGN KEY (store_id) REFERENCES store(store_id)  
)


CREATE TABLE customer (
  customer_id int identity(1, 1),
  first_name VARCHAR(45) NOT NULL,
  last_name VARCHAR(45) NOT NULL,
  email VARCHAR(50) DEFAULT NULL,
  address_id int NOT NULL,
  CONSTRAINT pk_customer PRIMARY KEY  (customer_id),
  CONSTRAINT fk1_customer FOREIGN KEY (address_id) REFERENCES address (address_id)
);


CREATE TABLE orderorder(
    order_id int IDENTITY(1,1) NOT NULL,
    staff_id int NOT NULL,
    customer_id int NOT NULL,
    order_date date DEFAULT (getdate()) NULL,
    CONSTRAINT pk_order PRIMARY KEY(order_id),
    CONSTRAINT fk_order FOREIGN KEY (staff_id) REFERENCES staff(staff_id),
    CONSTRAINT fk2_order FOREIGN KEY (customer_id) REFERENCES customer(customer_id)
)



CREATE TABLE inventory_order(
    inventory_order_id int IDENTITY(1,1) NOT NULL,
    order_id int NOT NULL,
    inventory_id int NOT NULL,
    quantity int NULL,
    CONSTRAINT pk_inventory_order PRIMARY KEY(inventory_order_id),
    CONSTRAINT fk1_inventory_order FOREIGN KEY (inventory_id) REFERENCES inventory(inventory_id),
    CONSTRAINT fk2_inventory_order FOREIGN KEY (order_id) REFERENCES orderorder(order_id)
)




